		if ($this->isColumnModified(DonacioPeer::DC)) $criteria->add(DonacioPeer::DC, $this->dc);
		if ($this->isColumnModified(DonacioPeer::ENTITAT_COMPTE)) $criteria->add(DonacioPeer::ENTITAT_COMPTE, $this->entitat_compte);
		if ($this->isColumnModified(DonacioPeer::TITULAR_COMPTE)) $criteria->add(DonacioPeer::TITULAR_COMPTE, $this->titular_compte);
		if ($this->isColumnModified(DonacioPeer::NEWSLETTER)) $criteria->add(DonacioPeer::NEWSLETTER, $this->newsletter);
		if ($this->isColumnModified(DonacioPeer::NB_PEDIDO)) $criteria->add(DonacioPeer::NB_PEDIDO, $this->nb_pedido);
		if ($this->isColumnModified(DonacioPeer::RESPONSE)) $criteria->add(DonacioPeer::RESPONSE, $this->response);

		return $criteria;
	}

	/**
	 * Builds a Criteria object containing the primary key for this object.
	 *
	 * Unlike buildCriteria() this method includes the primary key values regardless
	 * of whether or not they have been modified.
	 *
	 * @return     Criteria The Criteria object containing value(s) for primary key(s).
	 */
	public function buildPkeyCriteria()
	{
		$criteria = new Criteria(DonacioPeer::DATABASE_NAME);

		$criteria->add(DonacioPeer::ID, $this->id);

		return $criteria;
	}

	/**
	 * Returns the primary key for this object (row).
	 * @return     int
	 */
	public function getPrimaryKey()
	{
		return $this->getId();
	}

	/**
	 * Generic method to set the primary key (id column).
	 *
	 * @param      int $key Primary key.
	 * @return     void
	 */
	public function setPrimaryKey($key)
	{
		$this->setId($key);
	}

	/**
	 * Sets contents of passed object to values from current object.
	 *
	 * If desired, this method can also make copies of all associated (fkey referrers)
	 * objects.
	 *
	 * @param      object $copyObj An object of Donacio (or compatible) type.
	 * @param      boolean $deepCopy Whether to also copy all rows that refer (by fkey) to the current row.
	 * @throws     PropelException
	 */
	public function copyInto($copyObj, $deepCopy = false)
	{

		$copyObj->setNom($this->nom);

		$copyObj->setCognoms($this->cognoms);

		$copyObj->setEntitat($this->entitat);

		$copyObj->setDireccio($this->direccio);

		$copyObj->setCodiPostal($this->codi_postal);

		$copyObj->setCiutat($this->ciutat);

		$copyObj->setTelefon($this->telefon);

		$copyObj->setEmail($this->email);

		$copyObj->setProfessio($this->professio);

		$copyObj->setDni($this->dni);

		$copyObj->setDataNaixement($this->data_naixement);

		$copyObj->setDonacio($this->donacio);

		$copyObj->setPeriodicitat($this->periodicitat);

		$copyObj->setFormaPagament($this->forma_pagament);

		$copyObj->setCc($this->cc);

		$copyObj->setOficina($this->oficina);

		$copyObj->setDc($this->dc);

		$copyObj->setEntitatCompte($this->entitat_compte);

		$copyObj->setTitularCompte($this->titular_compte);

		$copyObj->setNewsletter($this->newsletter);

		$copyObj->setNbPedido($this->nb_pedido);

		$copyObj->setResponse($this->response);


		$copyObj->setNew(true);

		$copyObj->setId(NULL); // this is a auto-increment column, so set to default value

	}

	/**
	 * Makes a copy of this object that will be inserted as a new row in table when saved.
	 * It creates a new object filling in the simple attributes, but skipping any primary
	 * keys that are defined for the table.
	 *
	 * If desired, this method can also make copies of all associated (fkey referrers)
	 * objects.
	 *
	 * @param      boolean $deepCopy Whether to also copy all rows that refer (by fkey) to the current row.
	 * @return     Donacio Clone of current object.
	 * @throws     PropelException
	 */
	public function copy($deepCopy = false)
	{
		// we use get_class(), because this might be a subclass
		$clazz = get_class($this);
		$copyObj = new $clazz();
		$this->copyInto($copyObj, $deepCopy);
		return $copyObj;
	}

	/**
	 * Returns a peer instance associated with this om.
	 *
	 * Since Peer classes are not to have any instance attributes, this method returns the
	 * same instance for all member of this class. The method could therefore
	 * be static, but this would prevent one from overriding the behavior.
	 *
	 * @return     DonacioPeer
	 */
	public function getPeer()
	{
		if (self::$peer === null) {
			self::$peer = new DonacioPeer();
		}
		return self::$peer;
	}

